// import React from 'react';
import { useState } from 'react'; //importing the hook use state directly, no need for React..... in code


const Home = () => {
    const [name, setName] = useState('Paz');

    const handleClick = (e) => {        
        (name === 'Paz') ? setName('Ida') : setName('Paz');
    }

    return (
        <div className="home">
            <h2>Homepage</h2>
            <p>{name}</p>
            <button onClick={handleClick} >Click me</button>
        </div>
    );
}

export default Home;
